import React, { useState, useEffect } from 'react'
import './style.css'
const Todo = () => {


    //GET ITEMS FROM LOCALSTORAGE
    const localStorage1 = () => {
        const list = localStorage.getItem("todoList")
        if(list){
            return JSON.parse(list)
        }else{
            return([])
        }
    }

    const [name,setName] = useState({

        inpData:"",
        inpName:"",
    })

    // const [inpData, setInpData] = useState('')
    // const [inpName,setInpName] = useState('')

    // const [inpData2, setInpData2] = useState(localStorage1())
    // const [inpName2,setInpName2] = useState(localStorage1())

    const [isData,setIsData] = useState('')
    const [toggle,setToggle] = useState(false) 

    const dataFunc = (event) => {
        const value = event.target.value
        const name = event.target.name

        setName((preValue) => {

            return {
                ...preValue,
                [name] : value,
            }}
        // setInpName(e.target.value)
    )}
    const dataFunc1 = (e) => {
        // setInpData(e.target.value)
        setInpName(e.target.value)
    }

    //ADD ITEM
    const addItem = () => {
        if (!name.inpData) {
            alert("plz enter valid value")
        }else if(toggle && name.inpData){
            setInpData2(
                inpData2.map((curelem) => {
                    if(curelem.id === isData){
                        return {...curelem,name1:name.inpData}
                    }else{
                        return (curelem)
                    }
                })
            )
            setInpData('')
            setToggle(false)
        } 

        else {
            const myInputData = {
                id: new Date().getTime().toString(),
                name1: name.inpData,
                name2: inpName
            }
            localStorage.setItem("todoList", JSON.stringify(inpData2))
            setInpData2([...inpData2, myInputData])

            setInpData("")
        }

    }

    //DELETE ITEM
    const deletedItem = (index) => {
        const updatedItems = inpData2.filter((curElem) => {
            return (
                curElem.id !== index
            )
        })
        setInpData2(updatedItems)
    }

    //EDIT ITEM
    const edititem = (index) => {
        const editItem = inpData2.find((curElem) => {
            return(
                curElem.id === index
            )

        })
        setInpData(editItem.name1)
        setIsData(index)
        setToggle(true)
    }

    //CLEAR ALL FUNCTION
    const clearAll = () => {
        setInpData2([])
    }

    //SET ITEM ON LOCALSTORAGE
    useEffect(() => {
        localStorage.setItem("todoList", JSON.stringify(inpData2))
    }, [inpData2])


    return (
        <>
            <div className='whole'>
                <div className='container'>
                    <h1>CRUD - List</h1>

                </div>
                <div className="cont2">
                    <input type="text" placeholder='✋ Write Your First Name' onChange={dataFunc} value={name.inpData} className='inp'></input>
                    <input type="text" placeholder='✋ Write Your Last Name' onChange={dataFunc1} value={inpName} className='inp'></input>

                    <div>{toggle ? (<i className="fa111 fa11 fa1 far fa-edit" onClick={addItem}></i>) : 
                    <span className='plus'><i className="fa fa-plus add-button" onClick={addItem}></i></span>}</div>

                    {/* //SHOW THE LIST  */}
                    <table className='table'>
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">First</th>
                                <th scope="col">Last</th>
                                <th scope="col">Email</th>
                            </tr>
                        </thead>
                    </table>
                    {inpData2.map((curEle, index) => {
                        return (
                            // console.log(index),
                            <div className='showList1' key={index}>
                                <div className="showList">
                                    {/* <h4 className='para'>{curEle.name1}</h4> */}
                                    {/* <p className='para'>{curEle.name1}</p> */}
                                    {/* <p className='para'>{curEle.index}</p> */}
                                    {/* tables use */}
                                    <table className="table">

                                        <tbody>
                                            <tr className='para'>
                                                <th scope="row">{index+1}</th>
                                                <td scope='col'>{curEle.name1}</td>
                                                <td scope='col'>Otto</td>
                                                <td scope='col'>@mdo</td>
                                                <td><i className="fa11 fa1 far fa-edit" onClick={() => edititem(curEle.id)}></i></td>
                                                <td><i className="fa12 fa1 fa fa-trash" onClick={() => deletedItem(curEle.id)}></i></td>
                                            </tr>
                                            {/* <div className='btns'>
                                        <i className="fa11 fa1 far fa-edit"></i>
                                        <i className="fa12 fa1 fa fa-trash" onClick={() => deletedItem(curEle.id)}></i>
                                    </div> */}

                                        </tbody>
                                    </table>


                                </div>

                            </div>
                        )
                    })}

                </div>
                <div className='btnUp'>
                    <button className='btn effect04' dataText="Check List" dataTextHover="Remove all" onClick={clearAll}><span></span></button>
                </div>
            </div>
        </>
    )
}

export default Todo
