import React, { useState } from 'react'
import './style.css'
const Todo = () => {
    const [inpData, setInpData] = useState('')
    const [inpData2, setInpData2] = useState([])

    const dataFunc = (e) => {
        setInpData(e.target.value)
    }

    const addItem = () => {
        if(!inpData){
            alert("plz enter valid value")
        }else{
            setInpData2([...inpData2,inpData])
            setInpData2("")
        }
    }
    return (
        <>
            <div className='whole'>
                <div className='container'>
                    <h1>TODO - List</h1>

                </div>
                <div className="cont2">
                    <input type="text" placeholder='âœ‹ Write Something' onChange={dataFunc} value={inpData} className='inp'></input>
                    <span className='plus'><i className="fa fa-plus add-button" onClick={addItem}></i></span>
                    {/* //SHOW THE LIST  */}
                    {inpData2.map((curEle,index) => {
                        return(
                            <div className='showList1' key={index}>
                        <div className="showList">
                        <p className='para'>{curEle}</p>
                        <div>
                            <i className="fa11 fa1 far fa-edit"></i>
                            <i className="fa12 fa1 fa fa-trash"></i>
                        </div>
                        </div>

                    </div>
                        )
                    })}

                </div>
                <div className='btnUp'>
                    <button className='btn effect04' dataText="Check List" dataTextHover="Remove all" ><span></span></button>
                </div>
            </div>
        </>
    )
}

export default Todo
