import React, { useState } from 'react'

const Form = () => {
    const [fullName, setFullName] = useState({
        fname: "",
        lname: "",
        email: ""
    });


    const inputEvent = (event) => {
        // setName1(event.target.value)
        const value = event.target.value
        const name = event.target.name

        setFullName((preValue) => {

            return {
                ...preValue,
                [name] : value,
            }

            // if (name === 'fName') {
