import React, { useState } from 'react'

const Form = () => {
    const [name1,setName1] = useState('')
    const [name2,setName2] = useState('')
    const [name1New,setName1New] = useState('')
    const [name2New,setName2New] = useState('')

    const inputName1 = (event) => {
        setName1(event.target.value)
    }

    const inputName2 = (event) => {
        setName2(event.target.value)
    }

    const onSubmit = (e) => {
        e.preventDefault()
        setName1New(name1)
        setName2New(name2)
    }
  return (


    <>
    <form onSubmit={onSubmit}>
    <div style={{textAlign:"center" ,margin:"20px 20px"} }>
    <h1>Hello {name1New} {name2New}</h1><br />

    <input 
        type="text"
        placeholder="Enter Your Name"
        onChange={inputName1}
        value={name1}
    />
    <br /><br />
    <input 
        type="text"
        placeholder="Enter Your LastName"
        onChange={inputName2}
        value={name2}
    /><br /><br />
    <button>Submit</button>
    </div>

</form>
    </>
  )
}

export default Form
