import React, { useState, useEffect } from 'react'
import './style.css'
const Todo = () => {
    const [inpData, setInpData] = useState('')
    const [inpData2, setInpData2] = useState([])

    const dataFunc = (e) => {
        setInpData(e.target.value)
    }

    const addItem = () => {
        if (!inpData) {
            alert("plz enter valid value")
        } else {
            const myInputData = {
                id: new Date().getTime().toString(),
                name1: inpData
            }
            localStorage.setItem("todoList", JSON.stringify(inpData2))
            setInpData2([...inpData2, myInputData])

            setInpData("")
        }

    }

    const deletedItem = (index) => {
        const updatedItems = inpData2.filter((curElem) => {
            return (
                curElem.id !== index
            )
        })
        setInpData2(updatedItems)
    }

    const clearAll = () => {
        setInpData2([])
    }
    useEffect(() => {
        localStorage.setItem("todoList", JSON.stringify(inpData2))
    }, [inpData2])


    return (
        <>
            <div className='whole'>
                <div className='container'>
                    <h1>CRUD - List</h1>

                </div>
                <div className="cont2">
                    <input type="text" placeholder='✋ Write Something' onChange={dataFunc} value={inpData} className='inp'></input>
                    <span className='plus'><i className="fa fa-plus add-button" onClick={addItem}></i></span>
                    {/* //SHOW THE LIST  */}
                    <table className='table'>
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">First</th>
                                <th scope="col">Last</th>
                                <th scope="col">Email</th>
                            </tr>
                        </thead>
                    </table>
                    {inpData2.map((curEle, index) => {
                        return (
                            <div className='showList1' key={index}>
                                <div className="showList">
                                    {/* <h4 className='para'>{curEle.name1}</h4> */}
                                    {/* <p className='para'>{curEle.name1}</p> */}
                                    {/* <p className='para'>{curEle.index}</p> */}
                                    {/* tables use */}
                                    <table class="table">

                                        <tbody>
                                            <tr>
                                                <th scope="row">{curEle.id}</th>
                                                <td>{curEle.name1}</td>
                                                <td>Otto</td>
                                                <td>@mdo</td>
                                                <td><i className="fa11 fa1 far fa-edit"></i></td>
                                                <td><i className="fa12 fa1 fa fa-trash" onClick={() => deletedItem(curEle.id)}></i></td>
                                            </tr>

                                        </tbody>
                                    </table>




                                    {/* <div>
                                        <i className="fa11 fa1 far fa-edit"></i>
                                        <i className="fa12 fa1 fa fa-trash" onClick={() => deletedItem(curEle.id)}></i>
                                    </div> */}
                                </div>

                            </div>
                        )
                    })}

                </div>
                <div className='btnUp'>
                    <button className='btn effect04' dataText="Check List" dataTextHover="Remove all" onClick={clearAll}><span></span></button>
                </div>
            </div>
        </>
    )
}

export default Todo
